import React, { useState, useEffect } from 'react';
import BotonGenerarNumero from './BotonGenerarNumero';
import MostrarNumero from './MostrarNumero';

const MAX_NUMEROS = 5; // Número máximo de elementos en la lista

const GeneradorNumeros = () => {
  const [numeroGenerado, setNumeroGenerado] = useState(null);
  const [numerosGuardados, setNumerosGuardados] = useState([]);
  const [guardado, setGuardado] = useState(false);
  const [perdio, setPerdio] = useState(false);

  useEffect(() => {
    if (numerosGuardados.length > 1) {
      const esOrdenado = numerosGuardados.every((num, i) => i === 0 || num >= numerosGuardados[i - 1]);
      if (!esOrdenado) {
        setPerdio(true);
      }
    }
  }, [numerosGuardados]);

  const generarNumeroAleatorio = () => {
    const nuevoNumero = Math.floor(Math.random() * 100) + 1;
    setNumeroGenerado(nuevoNumero);
    setGuardado(false); // Desbloquear el botón de guardar al generar un nuevo número
  };

  const guardarNumero = () => {
    if (numeroGenerado !== null && numerosGuardados.length < MAX_NUMEROS) {
      const nuevaLista = [...numerosGuardados, numeroGenerado];
      nuevaLista.sort((a, b) => a - b); // Ordenar de menor a mayor
      setNumerosGuardados(nuevaLista);
      setGuardado(true); // Bloquear el botón de guardar después de guardar el número
    }
  };

  return (
    <div style={styles.container}>
      {perdio ? (
        <h1>¡Perdiste! No has logrado ordenar los números.</h1>
      ) : (
        <>
          <BotonGenerarNumero onClick={generarNumeroAleatorio} />
          {numeroGenerado && <MostrarNumero numero={numeroGenerado} />}
          <button onClick={guardarNumero} disabled={guardado || numerosGuardados.length === MAX_NUMEROS}>
            Guardar número
          </button>
          <div>
            <h2>Números guardados:</h2>
            <ul>
              {numerosGuardados.map((numero, index) => (
                <li key={index}>{numero}</li>
              ))}
            </ul>
          </div>
        </>
      )}
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    padding: '20px',
  },
};

export default GeneradorNumeros;
