import React, { useState } from 'react';

const NUM_CASILLAS = 5; // Número de casillas disponibles

const GeneradorNumeros = () => {
  const [numerosCasillas, setNumerosCasillas] = useState(Array(NUM_CASILLAS).fill(null));
  const [numeroGenerado, setNumeroGenerado] = useState(null);
  const [mensaje, setMensaje] = useState('');
  const [juegoTerminado, setJuegoTerminado] = useState(false);

  const generarNumeroAleatorio = () => {
    const nuevoNumero = Math.floor(Math.random() * 100) + 1;
    setNumeroGenerado(nuevoNumero);
    setMensaje(''); // Reiniciar mensaje
  };

  const guardarNumero = (index) => {
    if (!juegoTerminado && numeroGenerado !== null && numerosCasillas[index] === null) {
      const nuevaLista = [...numerosCasillas];
      nuevaLista[index] = numeroGenerado;
      setNumerosCasillas(nuevaLista);
      setNumeroGenerado(null); // Reiniciar el número generado

      // Validar orden de los números a partir del segundo número
      if (nuevaLista.filter(Boolean).length >= 2) {
        const numerosAlmacenados = nuevaLista.filter(Boolean);
        const esOrdenado = numerosAlmacenados.every((num, i) => i === 0 || num >= numerosAlmacenados[i - 1]);
        if (!esOrdenado) {
          setMensaje('¡Perdiste!');
          setJuegoTerminado(true);
        } else if (numerosAlmacenados.length === NUM_CASILLAS) {
          setMensaje('¡Ganaste!');
          setJuegoTerminado(true);
        }
      }
    }
  };

  const reiniciarJuego = () => {
    setNumerosCasillas(Array(NUM_CASILLAS).fill(null));
    setNumeroGenerado(null);
    setMensaje('');
    setJuegoTerminado(false);
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      textAlign: 'center',
    },
    casillasContainer: {
      display: 'flex',
      justifyContent: 'center',
      marginBottom: '20px',
    },
    casilla: {
      marginRight: '10px',
    },
    input: {
      width: '70px', 
      textAlign: 'center',
      borderRadius: '5px',
      border: '2px solid #000000', 
      padding: '10px', 
      margin: '0 5px', 
    },
    button: {
      height: '50px',
      width: '150px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      outline: 'none',
      cursor: 'pointer',
      backgroundColor: 'transparent',
      border: 'none',
      borderRadius: '30px',
      transition: 'all 0.2s ease',
      fontFamily: '"Source Sans Pro", sans-serif',
      fontSize: '14px',
      fontWeight: '600',
      color: '#606060',
      textShadow: '0 1px #fff',
      margin: '0 5px',
    },
    buttonHover: {
      backgroundColor: '#23c483',
      boxShadow: '0px 15px 20px rgba(46, 229, 157, 0.4)',
      color: '#fff',
      transform: 'translateY(-7px)',
    },
    buttonActive: {
      transform: 'translateY(-1px)',
    },
    mensaje: {
      fontSize: '20px',
      color: '#3498db', 
      fontWeight: 'bold',
    },
    reiniciarButton: {
      height: '50px',
      width: '200px',
      position: 'relative',
      backgroundColor: 'transparent',
      cursor: 'pointer',
      border: '2px solid #252525',
      overflow: 'hidden',
      borderRadius: '30px',
      color: '#333',
      transition: 'all 0.5s ease-in-out',
      margin: '0 5px',
    },
    btnTxt: {
      zIndex: '1',
      fontWeight: '800',
      letterSpacing: '4px',
    },
    type1After: {
      content: '""',
      position: 'absolute',
      left: '0',
      top: '0',
      transition: 'all 0.5s ease-in-out',
      backgroundColor: '#333',
      borderRadius: '30px',
      visibility: 'hidden',
      height: '10px',
      width: '10px',
      zIndex: '-1',
    },
    reiniciarButtonHover: {
      boxShadow: '1px 1px 200px #252525',
      color: '#fff',
      border: 'none',
    },
    type1AfterHover: {
      visibility: 'visible',
      transform: 'scale(100) translateX(2px)',
    },
  };

  return (
    <div style={styles.container}>
      <h1>Generador de Números Aleatorios</h1>
      <div>
        {numeroGenerado && <p>Número generado: {numeroGenerado}</p>}
      </div>
      <div style={styles.casillasContainer}>
        {numerosCasillas.map((numero, index) => (
          <div key={index} style={styles.casilla}>
            <input
              type="number"
              value={numero || ''}
              onChange={() => {}}
              disabled
              style={styles.input}
            />
            <button onClick={() => guardarNumero(index)} disabled={numeroGenerado === null || juegoTerminado} style={numeroGenerado === null || juegoTerminado ? styles.disabledButton : styles.button}>
              Guardar
            </button>
          </div>
        ))}
      </div>
      <button onClick={generarNumeroAleatorio} disabled={numeroGenerado !== null || juegoTerminado} style={{ ...styles.button }}>
        Generar número
      </button>
      {mensaje && <p style={styles.mensaje}>{mensaje}</p>}
      {juegoTerminado && (
        <div class="button type1" onClick={reiniciarJuego} style={{ ...styles.reiniciarButton }}>
          <span class="btn-txt" style={{ ...styles.btnTxt }}>Reiniciar</span>
          <span class="type1" style={{ ...styles.type1After }}></span>
        </div>
      )}
    </div>
  );
};

export default GeneradorNumeros;
