import React, { useState } from 'react';

const NUM_CASILLAS = 5; // Número de casillas disponibles

const GeneradorNumeros = () => {
  const [numerosCasillas, setNumerosCasillas] = useState(Array(NUM_CASILLAS).fill(null));
  const [numeroGenerado, setNumeroGenerado] = useState(null);
  const [mensaje, setMensaje] = useState('');
  const [juegoTerminado, setJuegoTerminado] = useState(false);

  const generarNumeroAleatorio = () => {
    const nuevoNumero = Math.floor(Math.random() * 100) + 1;
    setNumeroGenerado(nuevoNumero);
    setMensaje(''); // Reiniciar mensaje
  };

  const guardarNumero = (index) => {
    if (!juegoTerminado && numeroGenerado !== null && numerosCasillas[index] === null) {
      const nuevaLista = [...numerosCasillas];
      nuevaLista[index] = numeroGenerado;
      setNumerosCasillas(nuevaLista);
      setNumeroGenerado(null); // Reiniciar el número generado

      // Validar orden de los números a partir del segundo número
      if (nuevaLista.filter(Boolean).length >= 2) {
        const numerosAlmacenados = nuevaLista.filter(Boolean);
        const esOrdenado = numerosAlmacenados.every((num, i) => i === 0 || num >= numerosAlmacenados[i - 1]);
        if (!esOrdenado) {
          setMensaje('¡Perdiste!');
          setJuegoTerminado(true);
        } else if (numerosAlmacenados.length === NUM_CASILLAS) {
          setMensaje('¡Ganaste!');
          setJuegoTerminado(true);
        }
      }
    }
  };

  const reiniciarJuego = () => {
    setNumerosCasillas(Array(NUM_CASILLAS).fill(null));
    setNumeroGenerado(null);
    setMensaje('');
    setJuegoTerminado(false);
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      textAlign: 'center',
    },
    casillasContainer: {
      display: 'flex',
      justifyContent: 'center',
      marginBottom: '20px',
    },
    casilla: {
      marginRight: '10px',
    },
    input: {
      width: '70px', 
      textAlign: 'center',
      borderRadius: '5px',
      border: '2px solid #000000', 
      padding: '10px', 
      margin: '0 5px', 
    },
    button: {
      backgroundColor: '#4CAF50',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      padding: '10px 20px',
      cursor: 'pointer',
      transition: 'background-color 0.3s ease',
      margin: '0 5px', 
    },
    disabledButton: {
      backgroundColor: '#ccc',
      color: '#666',
      border: 'none',
      borderRadius: '5px',
      padding: '10px 20px',
      cursor: 'not-allowed',
      margin: '0 5px', 
    },
    mensaje: {
      fontSize: '20px',
      color: '#3498db', 
      fontWeight: 'bold',
    },
    generarButton: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      outline: 'none',
      cursor: 'pointer',
      width: '150px',
      height: '50px',
      backgroundImage: 'linear-gradient(to top, #D8D9DB 0%, #fff 80%, #FDFDFD 100%)',
      borderRadius: '30px',
      border: '1px solid #8F9092',
      transition: 'all 0.2s ease',
      fontFamily: '"Source Sans Pro", sans-serif',
      fontSize: '14px',
      fontWeight: '600',
      color: '#606060',
      textShadow: '0 1px #fff',
    },
    generarButtonHover: {
      boxShadow: '0 4px 3px 1px #FCFCFC, 0 6px 8px #D6D7D9, 0 -4px 4px #CECFD1, 0 -6px 4px #FEFEFE, inset 0 0 3px 3px #CECFD1',
    },
    generarButtonActive: {
      boxShadow: '0 4px 3px 1px #FCFCFC, 0 6px 8px #D6D7D9, 0 -4px 4px #CECFD1, 0 -6px 4px #FEFEFE, inset 0 0 5px 3px #999, inset 0 0 30px #aaa',
    },
    generarButtonFocus: {
      boxShadow: '0 4px 3px 1px #FCFCFC, 0 6px 8px #D6D7D9, 0 -4px 4px #CECFD1, 0 -6px 4px #FEFEFE, inset 0 0 5px 3px #999, inset 0 0 30px #aaa',
    },
  };

  return (
    <div style={styles.container}>
      <h1>Generador de Números Aleatorios</h1>
      <div>
        {numeroGenerado && <p>Número generado: {numeroGenerado}</p>}
      </div>
      <div style={styles.casillasContainer}>
        {numerosCasillas.map((numero, index) => (
          <div key={index} style={styles.casilla}>
            <input
              type="number"
              value={numero || ''}
              onChange={() => {}}
              disabled
              style={styles.input}
            />
            <button onClick={() => guardarNumero(index)} disabled={numeroGenerado === null || juegoTerminado} style={numeroGenerado === null || juegoTerminado ? styles.disabledButton : styles.button}>
              Guardar
            </button>
          </div>
        ))}
      </div>
      <button onClick={generarNumeroAleatorio} disabled={numeroGenerado !== null || juegoTerminado} style={numeroGenerado === null && !juegoTerminado ? styles.generarButton : { ...styles.button, ...styles.generarButton }}>
        Generar número
      </button>
      {mensaje && <p style={styles.mensaje}>{mensaje}</p>}
      {juegoTerminado && (
        <button onClick={reiniciarJuego} style={styles.button}>
          Reiniciar
        </button>
      )}
    </div>
  );
};

export default GeneradorNumeros;
