import React, { useState, useEffect } from 'react';
import '../generadorNumeros.css'; 
import '../spinner.css';

const NUM_CASILLAS = 5; 

const GeneradorNumeros = () => {
  const [numerosCasillas, setNumerosCasillas] = useState(Array(NUM_CASILLAS).fill(null));
  const [numeroGenerado, setNumeroGenerado] = useState(null);
  const [mensaje, setMensaje] = useState('');
  const [juegoTerminado, setJuegoTerminado] = useState(false);
  const [showFireworks, setShowFireworks] = useState(false);
  const [loading, setLoading] = useState(false);
  const [tiempoTranscurrido, setTiempoTranscurrido] = useState(0);
  const [temporizadorIniciado, setTemporizadorIniciado] = useState(false);
  const [tablaPosiciones, setTablaPosiciones] = useState([]);

  useEffect(() => {
    if (juegoTerminado && mensaje === '¡Ganaste!') {
      setShowFireworks(true);
      setTimeout(() => {
        setShowFireworks(false);
      }, 3000); 
      setTablaPosiciones(prevTablaPosiciones => [...prevTablaPosiciones, { tiempo: tiempoTranscurrido }]);
    }
  }, [juegoTerminado, mensaje, tiempoTranscurrido]);

  useEffect(() => {
    let timer = null;
    if (temporizadorIniciado && !juegoTerminado) {
      timer = setInterval(() => {
        setTiempoTranscurrido((prevTiempo) => prevTiempo + 1);
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [temporizadorIniciado, juegoTerminado]);

  const generarNumeroAleatorio = () => {
    setLoading(true);
    const nuevoNumero = Math.floor(Math.random() * 100) + 1;
    setNumeroGenerado(nuevoNumero);
    setMensaje('');
    setLoading(false);
    if (!temporizadorIniciado) {
      setTemporizadorIniciado(true);
    }
  };

  const guardarNumero = (index) => {
    if (!juegoTerminado && numeroGenerado !== null && numerosCasillas[index] === null) {
      const nuevaLista = [...numerosCasillas];
      nuevaLista[index] = numeroGenerado;
      setNumerosCasillas(nuevaLista);
      setNumeroGenerado(null);

      if (nuevaLista.filter(Boolean).length >= 2) {
        const numerosAlmacenados = nuevaLista.filter(Boolean);
        const esOrdenado = numerosAlmacenados.every((num, i) => i === 0 || num >= numerosAlmacenados[i - 1]);
        if (!esOrdenado) {
          setMensaje('¡You Lost!'); 
          setJuegoTerminado(true);
        } else if (numerosAlmacenados.length === NUM_CASILLAS) {
          setMensaje('¡you Won!');
          setJuegoTerminado(true);
        }
      }
    }
  };

  const reiniciarJuego = () => {
    setNumerosCasillas(Array(NUM_CASILLAS).fill(null));
    setNumeroGenerado(null);
    setMensaje('');
    setJuegoTerminado(false);
    setTiempoTranscurrido(0);
    setTemporizadorIniciado(false);
  };

  const formatTiempo = (segundos) => {
    const minutos = Math.floor(segundos / 60);
    const segundosRestantes = segundos % 60;
    return `${minutos.toString().padStart(2, '0')}:${segundosRestantes.toString().padStart(2, '0')}`;
  };

  return (
    <div className="container">
      {showFireworks && <div className="fireworks"></div>}
      <h1>Random Number Generator</h1>
      <div>
        <p><span>Generated number: </span>{numeroGenerado}</p>
        <p><span>Elapsed time: </span>{formatTiempo(tiempoTranscurrido)}</p>
      </div>
      <div className="numberCellsContainer">
        {numerosCasillas.map((numero, index) => (
          <div key={index} className="numberCell">
            <input
              type="number"
              value={numero || ''}
              onChange={() => {}}
              disabled
              className="input"
            />
            <button onClick={() => guardarNumero(index)} disabled={numeroGenerado === null || juegoTerminado} className={numeroGenerado === null || juegoTerminado ? "disabledButton" : "button"}>
              Save
            </button>
          </div>
        ))}
      </div>
      <button onClick={generarNumeroAleatorio} disabled={numeroGenerado !== null || juegoTerminado} className="generateButton">
        Generate number
      </button>
      {loading && <div className="loader"></div>}
      {mensaje && <p className={mensaje === '¡Perdiste!' ? 'lost' : 'message'}>{mensaje}</p>}
      {juegoTerminado && (
        <div>
          <p>Game Over!</p>
          <button onClick={reiniciarJuego} className="restartButton">
            <span className="btn-txt">Restart</span>
          </button>
        </div>
      )}
      <div className="footer">
        <div className="animacion">
          <div className="hourglassBackground">
            <div className="hourglassContainer">
              <div className="hourglassCurves"></div>
              <div className="hourglassCapTop"></div>
              <div className="hourglassGlassTop"></div>
              <div className="hourglassSand"></div>
              <div className="hourglassSandStream"></div>
              <div className="hourglassCapBottom"></div>
              <div className="hourglassGlass"></div>
            </div>
          </div>
        </div>
        <div className="contenedorTabla">
          <div className="tabla-posiciones">
            <h2>Tabla de Posiciones</h2>
            <table>
              <thead>
                <tr>
                  <th>Tiempo</th>
                </tr>
              </thead>
              <tbody>
                {tablaPosiciones.map((posicion, index) => (
                  <tr key={index}>
                    <td>{formatTiempo(posicion.tiempo)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GeneradorNumeros;
