// App.js
import React, { useState } from 'react';
import NumberGenerator from './NumberGenerator'; // Corrected import statement

const App = () => {
  const [showSpinner, setShowSpinner] = useState(false);

  const toggleSpinner = (show) => {
    setShowSpinner(show);
  };

  return (
    <div>
      {showSpinner && <div className="loader"></div>} {/* Spinner aqu√≠ */}
      <NumberGenerator toggleSpinner={toggleSpinner} />
    </div>
  );
};

export default App;
