import React, { useState } from 'react';

const NUM_CASILLAS = 5; // Número de casillas disponibles

const GeneradorNumeros = () => {
  const [numerosCasillas, setNumerosCasillas] = useState(Array(NUM_CASILLAS).fill(null));
  const [numeroGenerado, setNumeroGenerado] = useState(null);
  const [perdio, setPerdio] = useState(false);
  const [numerosGuardados, setNumerosGuardados] = useState(0);

  const generarNumeroAleatorio = () => {
    if (numerosGuardados < NUM_CASILLAS) {
      const nuevoNumero = Math.floor(Math.random() * 100) + 1;
      setNumeroGenerado(nuevoNumero);
      setPerdio(false); // Reiniciar el estado de pérdida
    }
  };

  const guardarNumero = (index) => {
    if (numeroGenerado !== null && numerosCasillas[index] === null) {
      const nuevaLista = [...numerosCasillas];
      nuevaLista[index] = numeroGenerado;
      setNumerosCasillas(nuevaLista);
      setNumerosGuardados(numerosGuardados + 1);

      const esOrdenado = nuevaLista.every((num, i) => i === 0 || num >= nuevaLista[i - 1]);
      if (!esOrdenado) {
        setPerdio(true);
      }
    }
  };

  return (
    <div style={styles.container}>
      <div>
        {numeroGenerado && <p>Número generado: {numeroGenerado}</p>}
      </div>
      <div style={styles.casillasContainer}>
        {numerosCasillas.map((numero, index) => (
          <div key={index} style={styles.casilla}>
            <input
              type="number"
              value={numero || ''}
              onChange={() => {}}
              disabled
              style={styles.input}
            />
            <button onClick={() => guardarNumero(index)} disabled={numeroGenerado === null || numero !== null}>
              Guardar
            </button>
          </div>
        ))}
      </div>
      <button onClick={generarNumeroAleatorio} disabled={numeroGenerado !== null || numerosGuardados === NUM_CASILLAS}>
        Generar número
      </button>
      {perdio && <p style={styles.mensajePerdio}>¡Perdiste! Has ingresado un número mayor que todos los números almacenados.</p>}
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    padding: '20px',
  },
  casillasContainer: {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: '20px',
  },
  casilla: {
    marginRight: '10px',
  },
  input: {
    width: '50px',
    textAlign: 'center',
  },
  mensajePerdio: {
    color: 'red',
    fontWeight: 'bold',
  },
};

export default GeneradorNumeros;
