// Fuera del componente GeneradorNumeros
import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import GeneradorNumeros from './GeneradorNumeros'; // Asegúrate de importar correctamente tu componente GeneradorNumeros

const SpinnerContainer = ({ showSpinner }) => {
  return showSpinner ? <div className="loader"></div> : null;
};

const App = () => {
  const [showSpinner, setShowSpinner] = useState(false);

  const toggleSpinner = (show) => {
    setShowSpinner(show);
  };

  return (
    <div>
      <SpinnerContainer showSpinner={showSpinner} />
      <GeneradorNumeros toggleSpinner={toggleSpinner} />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('root')); // Asegúrate de reemplazar 'root' con el ID de tu contenedor raíz en el HTML
