import React, { useState, useEffect } from 'react';
import '../generadorNumeros.css'; 
import '../spinner.css';

const NUM_CASILLAS = 5; 

const GeneradorNumeros = () => {
  const [numerosCasillas, setNumerosCasillas] = useState(Array(NUM_CASILLAS).fill(null));
  const [numeroGenerado, setNumeroGenerado] = useState(null);
  const [mensaje, setMensaje] = useState('');
  const [juegoTerminado, setJuegoTerminado] = useState(false);
  const [showFireworks, setShowFireworks] = useState(false);
  const [loading, setLoading] = useState(false); // Estado para mostrar o ocultar el spinner

  useEffect(() => {
    if (juegoTerminado && mensaje === '¡Ganaste!') {
      setShowFireworks(true);
      setTimeout(() => {
        setShowFireworks(false);
      }, 3000); 
    }
  }, [juegoTerminado, mensaje]);

  const generarNumeroAleatorio = () => {
    setLoading(true); // Mostrar spinner cuando se está generando un número
    const nuevoNumero = Math.floor(Math.random() * 100) + 1;
    setNumeroGenerado(nuevoNumero);
    setMensaje('');
    setLoading(false); // Ocultar spinner después de generar el número
  };

  const guardarNumero = (index) => {
    if (!juegoTerminado && numeroGenerado !== null && numerosCasillas[index] === null) {
      const nuevaLista = [...numerosCasillas];
      nuevaLista[index] = numeroGenerado;
      setNumerosCasillas(nuevaLista);
      setNumeroGenerado(null);

      if (nuevaLista.filter(Boolean).length >= 2) {
        const numerosAlmacenados = nuevaLista.filter(Boolean);
        const esOrdenado = numerosAlmacenados.every((num, i) => i === 0 || num >= numerosAlmacenados[i - 1]);
        if (!esOrdenado) {
          setMensaje('¡Perdiste!');
          setJuegoTerminado(true);
        } else if (numerosAlmacenados.length === NUM_CASILLAS) {
          setMensaje('¡Ganaste!');
          setJuegoTerminado(true);
        }
      }
    }
  };

  const reiniciarJuego = () => {
    setNumerosCasillas(Array(NUM_CASILLAS).fill(null));
    setNumeroGenerado(null);
    setMensaje('');
    setJuegoTerminado(false);
  };

  return (
    <div className="container">
      {showFireworks && <div className="fireworks"></div>}
      <h1>Random Number Generator</h1>
      <div>
        <p><span>Generated number: </span>{numeroGenerado}</p>
      </div>
      <div className="numberCellsContainer">
        {numerosCasillas.map((numero, index) => (
          <div key={index} className="numberCell">
            <input
              type="number"
              value={numero || ''}
              onChange={() => {}}
              disabled
              className="input"
            />
            <button onClick={() => guardarNumero(index)} disabled={numeroGenerado === null || juegoTerminado} className={numeroGenerado === null || juegoTerminado ? "disabledButton" : "button"}>
              Save
            </button>
          </div>
        ))}
      </div>
      <button onClick={generarNumeroAleatorio} disabled={numeroGenerado !== null || juegoTerminado} className="generateButton">
        Generate number
      </button>
      {loading && <div className="loader"></div>} {/* Agregamos el spinner aquí */}
      {mensaje && <p className={mensaje === '¡Perdiste!' ? 'lost' : 'message'}>{mensaje}</p>}
      {juegoTerminado && (
        <button onClick={reiniciarJuego} className="restartButton">
          <span className="btn-txt">Restart</span>
        </button>
      )}
      {/* Simulación de footer */}
      <footer>
      <main id="container">
        
        <div className="dots">
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
            <div className="dot"></div>
            <div class="dot"></div>
        </div>
        <div class="dots2">
            <div class="dot2"></div>
            <div class="dot2"></div>
            <div class="dot2"></div>
            <div class="dot2"></div>
            <div class="dot2"></div>
            <div class="dot2"></div>
            <div class="dot2"></div>
            <div class="dot2"></div>
            <div class="dot2"></div>
            <div class="dot2"></div>
        </div>
        <div class="circle"></div>
    </main>      </footer>
    </div>
  );
};

export default GeneradorNumeros;
