import React, { useState, useEffect } from 'react';
import '../numberGenerator.css'; 
import '../spinner.css';

const NUM_CELLS = 5; 

const NumberGenerator = () => {
  const [cellNumbers, setCellNumbers] = useState(Array(NUM_CELLS).fill(null));
  const [generatedNumber, setGeneratedNumber] = useState(null);
  const [message, setMessage] = useState('');
  const [gameOver, setGameOver] = useState(false);
  const [showFireworks, setShowFireworks] = useState(false);
  const [loading, setLoading] = useState(false); // State to show or hide the spinner

  useEffect(() => {
    if (gameOver && message === 'You won!') {
      setShowFireworks(true);
      setTimeout(() => {
        setShowFireworks(false);
      }, 3000); 
    }
  }, [gameOver, message]);

  const generateRandomNumber = () => {
    setLoading(true); // Show spinner while generating a number
    const newNumber = Math.floor(Math.random() * 100) + 1;
    setGeneratedNumber(newNumber);
    setMessage('');
    setLoading(false); // Hide spinner after generating the number
  };

  const saveNumber = (index) => {
    if (!gameOver && generatedNumber !== null && cellNumbers[index] === null) {
      const newList = [...cellNumbers];
      newList[index] = generatedNumber;
      setCellNumbers(newList);
      setGeneratedNumber(null);

      if (newList.filter(Boolean).length >= 2) {
        const storedNumbers = newList.filter(Boolean);
        const isOrdered = storedNumbers.every((num, i) => i === 0 || num >= storedNumbers[i - 1]);
        if (!isOrdered) {
          setMessage('You lost!');
          setGameOver(true);
        } else if (storedNumbers.length === NUM_CELLS) {
          setMessage('You won!');
          setGameOver(true);
        }
      }
    }
  };

  const restartGame = () => {
    setCellNumbers(Array(NUM_CELLS).fill(null));
    setGeneratedNumber(null);
    setMessage('');
    setGameOver(false);
  };

  return (
    <div className="container">
      {showFireworks && <div className="fireworks"></div>}
      <h1>Random Number Generator</h1>
      <div>
        <p><span>Generated number: </span>{generatedNumber}</p>
      </div>
      <div className="numberCellsContainer">
        {cellNumbers.map((number, index) => (
          <div key={index} className="numberCell">
            <input
              type="number"
              value={number || ''}
              onChange={() => {}}
              disabled
              className="input"
            />
            <button onClick={() => saveNumber(index)} disabled={generatedNumber === null || gameOver} className={generatedNumber === null || gameOver ? "disabledButton" : "button"}>
              Save
            </button>
          </div>
        ))}
      </div>
      <button onClick={generateRandomNumber} disabled={generatedNumber !== null || gameOver} className="generateButton">
        Generate number
      </button>
      {loading && <div className="loader"></div>} {/* Add the spinner here */}
      {message && <p className={message === 'You lost!' ? 'lost' : 'message'}>{message}</p>}
      {gameOver && (
        <button onClick={restartGame} className="restartButton">
          <span className="btn-txt">Restart</span>
        </button>
      )}
      {/* Footer simulation */}
      <footer>
      <main id="container">
      <div className="hourglassBackground">
      <div className="hourglassContainer">
        <div className="hourglassCurves"></div>
        <div className="hourglassCapTop"></div>
        <div className="hourglassGlassTop"></div>
        <div className="hourglassSand"></div>
        <div class="hourglassSandStream"></div>
        <div class="hourglassCapBottom"></div>
        <div class="hourglassGlass"></div>
      </div>
    </div>
    </main>     
     </footer>
    </div>
  );
};

export default NumberGenerator;
