import React, { useState } from 'react';

const NUM_CASILLAS = 5; // Número de casillas disponibles

const GeneradorNumeros = () => {
  const [numerosCasillas, setNumerosCasillas] = useState(Array(NUM_CASILLAS).fill(null));
  const [numeroGenerado, setNumeroGenerado] = useState(null);
  const [mensaje, setMensaje] = useState('');
  const [juegoTerminado, setJuegoTerminado] = useState(false);

  const generarNumeroAleatorio = () => {
    const nuevoNumero = Math.floor(Math.random() * 100) + 1;
    setNumeroGenerado(nuevoNumero);
    setMensaje(''); // Reiniciar mensaje
  };

  const reiniciarJuego = () => {
    setNumerosCasillas(Array(NUM_CASILLAS).fill(null));
    setNumeroGenerado(null);
    setMensaje('');
    setJuegoTerminado(false);
  };

  return (
    <div style={styles.container}>
      <h1>Generador de Números Aleatorios</h1>
      <div>
        {numeroGenerado && <p>Número generado: {numeroGenerado}</p>}
      </div>
      <div style={styles.casillasContainer}>
        {numerosCasillas.map((numero, index) => (
          <div key={index} style={styles.casilla}>
            <input
              type="number"
              value={numero || ''}
              onChange={() => {}}
              disabled
              style={styles.input}
            />
          </div>
        ))}
      </div>
      <button onClick={generarNumeroAleatorio} disabled={numeroGenerado !== null || juegoTerminado} style={numeroGenerado !== null || juegoTerminado ? styles.disabledButton : styles.button}>
        Generar número
      </button>
      {mensaje && <p>{mensaje}</p>}
      {juegoTerminado && (
        <button onClick={reiniciarJuego} style={styles.button}>
          Reiniciar
        </button>
      )}
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    textAlign: 'center',
  },
  casillasContainer: {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: '20px',
  },
  casilla: {
    marginRight: '10px',
  },
  input: {
    width: '50px',
    textAlign: 'center',
    borderRadius: '5px',
    border: '1px solid #ccc',
    padding: '5px',
  },
  button: {
    position: 'relative',
    fontFamily: 'Arial, Helvetica, sans-serif',
    fontWeight: 'bold',
    color: 'white',
    backgroundColor: '#171717',
    padding: '1em 2em',
    border: 'none',
    borderRadius: '.6rem',
    cursor: 'pointer',
    overflow: 'hidden',
    marginBottom: '10px', // Añadido para espaciado entre botones
    transition: 'all 0.3s ease-in-out',
  },
  disabledButton: {
    backgroundColor: '#ccc',
    color: '#666',
    border: 'none',
    borderRadius: '5px',
    padding: '10px 20px',
    cursor: 'not-allowed',
    margin: '0 5px',
  },
};

export default GeneradorNumeros;
