import React, { useState } from 'react';
import BotonGenerarNumero from './BotonGenerarNumero';

const NUM_CASILLAS = 5; // Número de casillas disponibles

const GeneradorNumeros = () => {
  const [numerosCasillas, setNumerosCasillas] = useState(Array(NUM_CASILLAS).fill(null));
  const [numeroGenerado, setNumeroGenerado] = useState(null);
  const [perdio, setPerdio] = useState(false);

  const generarNumeroAleatorio = () => {
    const nuevoNumero = Math.floor(Math.random() * 100) + 1;
    setNumeroGenerado(nuevoNumero);
    setPerdio(false); // Reiniciar el estado de pérdida
  };

  const guardarNumero = () => {
    if (numeroGenerado !== null) {
      const nuevaLista = [...numerosCasillas];
      nuevaLista.sort((a, b) => a - b); // Ordenar de menor a mayor
      const indexVacio = nuevaLista.indexOf(null);
      if (indexVacio !== -1) {
        nuevaLista[indexVacio] = numeroGenerado;
        setNumerosCasillas(nuevaLista);
        const esOrdenado = nuevaLista.every((num, i) => i === 0 || num >= nuevaLista[i - 1]);
        if (!esOrdenado) {
          setPerdio(true);
        }
      }
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.casillasContainer}>
        {numerosCasillas.map((numero, index) => (
          <div key={index} style={styles.casilla}>
            <input
              type="number"
              value={numero || ''}
              onChange={() => {}}
              disabled
              style={styles.input}
            />
          </div>
        ))}
      </div>
      <BotonGenerarNumero onClick={generarNumeroAleatorio} />
      {numeroGenerado && !perdio && (
        <button onClick={guardarNumero} style={styles.botonGuardar}>
          Guardar número
        </button>
      )}
      {perdio && <p style={styles.mensajePerdio}>¡Perdiste! Has ingresado un número mayor que todos los números almacenados.</p>}
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    padding: '20px',
  },
  casillasContainer: {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: '20px',
  },
  casilla: {
    marginRight: '10px',
  },
  input: {
    width: '50px',
    textAlign: 'center',
  },
  botonGuardar: {
    backgroundColor: '#3498db',
    color: '#fff',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    marginTop: '10px',
  },
  mensajePerdio: {
    color: 'red',
    fontWeight: 'bold',
  },
};

export default GeneradorNumeros;
