import React, { useState } from 'react';

const NUM_CASILLAS = 5; // Número de casillas disponibles

const GeneradorNumeros = () => {
  const [numerosCasillas, setNumerosCasillas] = useState(Array(NUM_CASILLAS).fill(null));
  const [numeroGenerado, setNumeroGenerado] = useState(null);
  const [mensaje, setMensaje] = useState('');
  const [juegoTerminado, setJuegoTerminado] = useState(false);

  const generarNumeroAleatorio = () => {
    const nuevoNumero = Math.floor(Math.random() * 100) + 1;
    setNumeroGenerado(nuevoNumero);
    setMensaje(''); // Reiniciar mensaje
  };

  const guardarNumero = (index) => {
    if (!juegoTerminado && numeroGenerado !== null && numerosCasillas[index] === null) {
      const nuevaLista = [...numerosCasillas];
      nuevaLista[index] = numeroGenerado;
      setNumerosCasillas(nuevaLista);
      setNumeroGenerado(null); // Reiniciar el número generado

      // Validar orden de los números a partir del segundo número
      if (nuevaLista.filter(Boolean).length >= 2) {
        const numerosAlmacenados = nuevaLista.filter(Boolean);
        const esOrdenado = numerosAlmacenados.every((num, i) => i === 0 || num >= numerosAlmacenados[i - 1]);
        if (!esOrdenado) {
          setMensaje('¡Perdiste!');
          setJuegoTerminado(true);
        } else if (numerosAlmacenados.length === NUM_CASILLAS) {
          setMensaje('¡Ganaste!');
          setJuegoTerminado(true);
        }
      }
    }
  };

  const reiniciarJuego = () => {
    setNumerosCasillas(Array(NUM_CASILLAS).fill(null));
    setNumeroGenerado(null);
    setMensaje('');
    setJuegoTerminado(false);
  };

  return (
    <div style={styles.container}>
      <div>
        {numeroGenerado && <p>Número generado: {numeroGenerado}</p>}
      </div>
      <div style={styles.casillasContainer}>
        {numerosCasillas.map((numero, index) => (
          <div key={index} style={styles.casilla}>
            <input
              type="number"
              value={numero || ''}
              onChange={() => {}}
              disabled
              style={styles.input}
            />
            <button onClick={() => guardarNumero(index)} disabled={numeroGenerado === null || juegoTerminado} style={styles.button}>
              Guardar
            </button>
          </div>
        ))}
      </div>
      <button onClick={generarNumeroAleatorio} disabled={numeroGenerado !== null || juegoTerminado} style={styles.button}>
        Generar número
      </button>
      {mensaje && <p style={styles.mensaje}>{mensaje}</p>}
      {juegoTerminado && (
        <button onClick={reiniciarJuego} style={{ ...styles.button, ...styles.reiniciarBtn }}>
          Reiniciar
        </button>
      )}
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    padding: '20px',
    backgroundColor: '#333', // Fondo gris oscuro
    borderRadius: '10px',
    boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.3)', // Efecto de sombra
  },
  casillasContainer: {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: '20px',
  },
  casilla: {
    marginRight: '10px',
  },
  input: {
    width: '50px',
    textAlign: 'center',
  },
  mensaje: {
    fontWeight: 'bold',
    marginTop: '10px',
    color: 'white', // Texto blanco
  },
  button: {
    backgroundColor: 'white',
    color: '#333',
    border: '1px solid #ccc',
    borderRadius: '10px', // Botones redondeados
    padding: '10px 20px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease', // Efecto de transición
    margin: '0 5px',
  },
  reiniciarBtn: {
    backgroundColor: 'lightblue',
    border: '1px solid #ccc',
    color: '#333',
  },
};

export default GeneradorNumeros;
